import React, { useState } from 'react'
import styled from 'styled-components'

const Main = styled.div`
    border-radius: 4px;
    width: 200px;
    height: 100px;    
    margin-right: 2%;
    position: relative;
    background-image: url("https://trello-backgrounds.s3.amazonaws.com/SharedBackground/480x320/c48e502274322eb1bf5dfada1bbfeda0/photo-1560037962-08931d95007f.jpg");
    &:hover{
        cursor: pointer;
        i{
            display: inline;

        }
    }
    i{
        position: absolute;
        bottom: 0;
        right: 0;
        padding: 10px;
        color: yellow;
        display: none;
    }
    .without{
        color: #fff;
        text-shadow: 1px 1px 1px #ccc !important;
    }
`

const Title = styled.p`
    position: absolute;
    top: 0;
    left: 0;
    margin-left: 10px;
    font-weight: bold;
    color: #fff;
`

const Board = (props) => {

    const [board, setBoard] = useState({board} = props)

    return (
        <Main>
            <Title>{props.name}</Title>
            {props.favorite ? <i className="fa fa-star"></i> : <i className="fa fa-star without"></i>}
        </Main>
    )
}

export default Board
