import React, { useState } from 'react'
import styled from 'styled-components'
import './list.css'

const Main = styled.div`
    background-color: #EBECF0;
    border-radius: 4px;
    padding: 5px 10px;
    width: 250px;    
    height: 100%;
    display: flex;
    flex-wrap: wrap;
`

const TopBottom = styled.div`
    background-color: transparent;
    width: 100%;
    height: 60px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`

const Title = styled.p`
    color: #172B52;
    font-size: 1em;
    font-weight: bold;  
    width: 90%;    
`

const ButtonStyled = styled.button`
    display: flex;
    align-items: center;
    background-color: ${props => props.backgroundColor || 'transparent'};  
    padding: 10px 10px;
    width: ${props => props.width || '100%'};
    border: none;      
    color: #41465F;
    &:hover{
        background-color: rgba(189, 195, 199,0.2);
        font-weight: bold;
        cursor: pointer;        
    }
`

const ActionButton = styled.button`    
    background-color: ${props => props.backgroundColor || 'green'};
    padding: 10px 10px;
    border-radius: 5px;
    border: none;
    color: #fff;    
    font-size: 1.0em;
    width: ${props => props.width || '50'}%;
    margin-right: ${props => props.marginRight || 0}%;
    &:hover{
        cursor: pointer;
        font-weight: bold;
    }
`

const TitleButton = styled.p`
    font-size: 1em;
    margin-left: 5px;    
`


const DivAddCard = styled.div`
    width: 100%;    
    height: auto;
    margin: 10px 0px;
    border-radius: 4px;
    display: grid;
    grid-template-rows: 100px 80px;    
`

const DivActionAddCards = styled.div`
    display: flex;
    align-items: center;
`

const InputAddCard = styled.input`
    border: none;
    border-radius: 4px;
    width: 100%;
    height: 100%;
    border: 1px solid black;
`


const List = (props) => {

    const [isAdd, setIsAdd] = useState(false)

    function addCard() {
        setIsAdd(!isAdd)
    }

    return (
        <Main>
            <TopBottom>
                <Title>Lista 1</Title>
                <i className="fa fa-ellipsis-h"></i>
            </TopBottom>
            <TopBottom>
                <ButtonStyled onClick={addCard}>
                    <i className="fa fa-plus"></i>
                    <TitleButton>Adicionar outro cart√£o</TitleButton>
                </ButtonStyled>
            </TopBottom>
            <DivAddCard>
                <div>
                    <InputAddCard></InputAddCard>
                </div>
                <DivActionAddCards>                    
                    <ActionButton width="50" marginRight="3">Salvar</ActionButton>
                    <i className="fa fa-close"></i>
                </DivActionAddCards>
            </DivAddCard>
        </Main>
    )
}

export default List
