import React, {useEffect, useState} from 'react'
import {useParams} from 'react-router-dom'
import styled from 'styled-components'
import axios from 'axios'

const Main = styled.div`
    grid-area: content;
    display: grid;
    grid-template-rows: 6vh 85vh;
`

const SubNav = styled.div`
    height: 100%;
    width: 100%;
    background-color: black;
    padding: 5px;
    display: flex;
    align-items: center;
    i{
        color: white;
        font-size: 1.6em;
        margin-left: 11px;
    }
    .isFavorite{
        color: #f1c40f;
    }
`
const SubContent = styled.div`
    background-color: green;
    height: 100%;
    width: 100%;
`

const BoardName = styled.p`
    color: white;
    font-weight: bold;
    font-size: 1em;
    padding: 5px;
`

const Board = () => {

    const {id} = useParams()

    const [board, setBoard] = useState({name: '', favorite: false, _id: ''})

    useEffect(() => {
        console.log(`http://localhost:4000/boards/${id}`)
        axios.get(`http://localhost:4000/boards/${id}`).then((response) => setBoard(response.data))
    }, [])

    return(
        <Main>
            <SubNav>
                <BoardName>{board.name}</BoardName>
                <i className="fa fa-star"></i>
            </SubNav>
            <SubContent>

            </SubContent>
        </Main>
    )
}

export default Board
