import React from 'react'
import styled from 'styled-components'

const StyledLI = styled.li`
    display: flex;
    background-color: white;
    width: 100%;
    padding: 5px;
    align-items: center;
    height: 20px;
    margin: 10%; 
    color: ${props => props.selected ? 'rgba(41, 128, 185,1.0)' : 'black'};
    background-color: ${props => props.selected ? 'rgba(52, 152, 219,0.4)' : 'white'};
    border-radius: 5px;
`

const LIText = styled.p`
    font-weight: 600;
    font-size: 1.0em;
    width: 85%;
    margin-left: 15px;    
`

const Item = (props) => {

    return(
        <StyledLI selected={props.selected}>
            <i className={props.icon}></i>
            <LIText>{props.text}</LIText>
        </StyledLI>
    )
}

export default Item
