import React, { useState } from 'react'
import styled from 'styled-components'
import './list.css'
import Card from '../card/card'

const Main = styled.div`
    background-color: #EBECF0;
    border-radius: 4px;
    padding: 5px 10px;
    width: 250px;    
    height: 100%;
    display: flex;
    flex-wrap: wrap;
`

const TopBottom = styled.div`
    background-color: transparent;
    width: 100%;
    height: 60px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`

const Title = styled.p`
    color: #172B52;
    font-size: 1em;
    font-weight: bold;  
    width: 90%;    
`

const ButtonStyled = styled.button`
    display: flex;
    align-items: center;
    background-color: ${props => props.backgroundColor || 'transparent'};  
    padding: 10px 10px;
    width: ${props => props.width || '100%'};
    border: none;      
    color: #41465F;
    &:hover{
        background-color: rgba(189, 195, 199,0.2);
        font-weight: bold;
        cursor: pointer;        
    }
`

const ActionButton = styled.button`    
    background-color: ${props => props.backgroundColor || 'green'};
    padding: 10px 10px;
    border-radius: 5px;
    border: none;
    color: #fff;    
    font-size: 1.0em;
    width: ${props => props.width || '50'}%;
    margin-right: ${props => props.marginRight || 0}%;
    &:hover{
        cursor: pointer;
        font-weight: bold;
    }
`

const TitleButton = styled.p`
    font-size: 1em;
    margin-left: 5px;    
`


const DivAddCard = styled.div`
    width: 100%;    
    height: auto;
    margin: 10px 0px;
    border-radius: 4px;
    display: grid;
    grid-template-rows: 100px 80px;    
`

const DivActionAddCards = styled.div`
    display: flex;
    align-items: center;
`

const InputAddCard = styled.input`
    border: none;
    border-radius: 4px;
    width: 100%;
    height: 100%;
`

const DivContent = styled.div`
    margin: 10px 0px;
    height: auto;
    display: flex;
    flex-wrap: wrap;
    width: 100%;
`

const Input = styled.input`
    border: none;
    width: 90%;
    height: 40px;
    border-radius: 5px;
    &:focus{
        border-bottom: 1px solid blue;
    }
`


const List = (props) => {

    const [isAdd, setIsAdd] = useState(false)
    const [isEditList, setIsEditList] = useState(false)
    const [title, setTitle] = useState(props.title)

    function addCard() {
        setIsAdd(!isAdd)
    }

    function editList(){
        setIsEditList(!isEditList)
    }    

    function renderTitleCard(e){
        setTitle(e.target.value)
    }

    function keyDownTitle(e){
        if(e.key === 'Enter'){
            editList()
        }
    }

    return (
        <Main>
            <TopBottom>
                {
                    !isEditList ? 
                    <Title onClick={editList}>{title}</Title>
                    :
                    <Input value={title} onChange={renderTitleCard} onKeyDown={keyDownTitle} onBlur={editList}></Input>
                }                
                <i className="fa fa-ellipsis-h"></i>
            </TopBottom>
            <DivContent>
                <Card message="Teste" />
                <Card message="Teste" />
                <Card message="Teste" />
            </DivContent>
            {
                !isAdd ?
                    <TopBottom>
                        <ButtonStyled onClick={addCard}>
                            <i className="fa fa-plus"></i>
                            <TitleButton>Adicionar outro cartão</TitleButton>
                        </ButtonStyled>
                    </TopBottom>
                    :
                    <DivAddCard>
                        <div>
                            <InputAddCard placeholder="Digite o nome do cartão"></InputAddCard>
                        </div>
                        <DivActionAddCards>
                            <ActionButton width="50" marginRight="3">Salvar</ActionButton>
                            <i className="fa fa-close"  onClick={addCard}></i>
                        </DivActionAddCards>
                    </DivAddCard>
            }
        </Main>
    )
}

export default List
