import React, { useState } from 'react'
import styled from 'styled-components'

const Main = styled.div`
    background-color: #EBECF0;
    border-radius: 4px;
    padding: 5px 10px;
    width: 250px;    
`

const TopBottom = styled.div`
    background-color: transparent;
    width: 100%;
    height: 60px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`

const Title = styled.p`
    color: #172B52;
    font-size: 1em;
    font-weight: bold;  
    width: 90%;    
`

const ButtonStyled = styled.button`
    display: flex;
    align-items: center;
    background-color: transparent;  
    padding: 10px 10px;
    width: 100%;
    border: none;      
    color: #41465F;
    &:hover{
        background-color: rgba(189, 195, 199,0.2);
        font-weight: bold;
        cursor: pointer;        
    }
`

const TitleButton = styled.p`
    font-size: 1em;
    margin-left: 5px;    
` 

const Content = styled.div`

`

const DivAddCard = styled.div`
    width: 100%;
    background-color: white;
    height: 50px;
    margin: 10px 0px;
    border-radius: 4px;
    display: flex;
`

const ButtonSave = styled.button`
    border: none;
    background-color: rgba(39, 174, 96,1.0);
    &:hover{
        background-color: rgba(46, 204, 113,1.0);
    }
`

const List = (props) => {

    const [isAdd, setIsAdd] = useState(false)

    function addCard(){
        setIsAdd(!isAdd)
    }

    return(
        <Main>
            <TopBottom>
                <Title>Lista 1</Title>
                <i className="fa fa-ellipsis-h"></i>
            </TopBottom>
            <TopBottom>
                <ButtonStyled onClick={addCard}>                    
                    <i className="fa fa-plus"></i>
                    <TitleButton>Adicionar outro cart√£o</TitleButton>
                </ButtonStyled>
            </TopBottom>            
            <DivAddCard>

            </DivAddCard>
        </Main>
    )
}

export default List
