    padding: 5px;
    border: none;
    background-color: transparent;
    &:hover{
        background-color: rgb(217, 219, 226);
        border-radius: 2px;
    }
    i{
        color: #717D92;
    }
    p{
        font-size: 0.7em;
        margin-left: 5px;
        color: #717D92;
    }
`

const Content = styled.div`
    display: flex;
    height: auto;
    padding: 5px;
    width: 100%;
`

